<?phpinclude_once('Controller.php');//// Базовый контроллер сайта.//abstract class C_Base extends Controller{	protected $title;		// заголовок страницы	protected $content;    // содержание страницы    protected $articles;    //	// Конструктор.	//	function __construct()	{			}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		$this->title = 'Название сайта';		$this->content = '';	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('title' => $this->title, 'content' => $this->content);		$page = $this->Template('View/v_main.php', $vars);		echo $page;	}	protected function Startup()    {        // DB connection config.        $hostname = 'localhost';        $username = 'root';        $password = '';        $dbName = 'blog2';        // Connect to db.        mysql_connect($hostname, $username, $password) or die('No connect with data base');        mysql_query('SET NAMES cp1251');        mysql_select_db($dbName) or die('No data base');        // Session start.        session_start();    }}