<?phpinclude_once('C_Base.php');include_once('model.php');//// Контроллер страницы редактирования.//class C_Edit extends C_Base{    protected $article_title; //new added article title    public $id; //article id    protected $article_content; //new added article text    protected $id_article;//new added article	private $error;	// сообщение об ошибке	//	// Конструктор.	//	function __construct()	{	    $id_article = $_GET['id'];        $db_article = Model::articles_get($id_article);        foreach($db_article as $one_article){            $this->article_title = $one_article['title'];            $this->article_content = $one_article['content'];        }	}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()    {        parent::OnInput();        $this->page_title = $this->page_title . ' :: This article edit';        if (isset($_POST['Save'])) {            Model::articles_edit($_GET['id'], $_POST['article_title'], $_POST['article_content']);            header('Location: index.php?c=Editor');        }        if(isset($_POST['Delete'])) {            Model::articles_delete($_GET['id']);            header('Location: index.php?c=Editor');        }    }	//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('page_title'=> $this->page_title, 'article_title'=>$this->article_title,            'article_content'=>$this->article_content,'error' => $this->error);		$page = Controller::Template('View/v_edit.php', $vars);        echo $page;	}	}/*$id_article = $_GET['id'];ARTIC_all :: articles_get($id_article);$db_article = ARTIC_all::articles_get($id_article);foreach($db_article as $one_article){    $x = $one_article['title'];    $y = $one_article['content'];}if (isset($_POST['edit'])){    ARTIC_all::articles_edit($_GET['id'],$_POST['title'],$_POST['content']);    header('Location: v_editor.php');}if(isset($_POST['delete'])){    ARTIC_all::articl