<?phpinclude_once('Controller/C_Base.php');include_once('Model/model.php');//// Конттроллер страницы чтения.//class C_View extends C_Base{	public $text;	// текст	//	// Конструктор.	//	function __construct()	{        parent::Startup();	}    //	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		parent::OnInput();        $this->articles = Model::articles_all();		$this->title = $this->title . ' :: Чтение';        $this->text = $this->articles;	}	//	// Виртуальный генератор HTML.	//		protected function OnOutput()    {        $vars = array('text' =>$this->text);        $this->content = $this->Template('View/v_view.php', $vars);        parent::OnOutput();    }}