<?php//// Список всех статей//function articles_all(){	// Запрос.	$query = "SELECT * FROM articles ORDER BY id_article DESC";	$result = mysql_query($query);								if (!$result)		die(mysql_error());		// Извлечение из БД.	$n = mysql_num_rows($result);	$articles = array();	for ($i = 0; $i < $n; $i++)	{		$row = mysql_fetch_assoc($result);		$articles[] = $row;	}		return $articles;}//// Конкретная статья//function articles_get($id_article){    $query = "SELECT title,content FROM articles WHERE id_article = $id_article";    $result = mysql_query($query);    if (!$result)        die(mysql_error());    $n = mysql_num_rows($result);    $id_article = array();    for ($i = 0; $i < $n; $i++)    {        $row = mysql_fetch_assoc($result);        $id_article[] = $row;    }	return $id_article;}//// Добавить статью//function articles_new($title, $content){	// Подготовка.	$title = trim($title);	$content = trim($content);	// Проверка.	if ($title == '')		return false;		// Запрос.	$t = "INSERT INTO articles (title, content) VALUES ('%s', '%s')";		$query = sprintf($t, 	                 mysql_real_escape_string($title),	                 mysql_real_escape_string($content));		$result = mysql_query($query);								if (!$result)		die(mysql_error());			return true;}//// Изменить статью//function articles_edit($id_article, $title, $content){	// TODO}//// Удалить статью//function articles_delete($id_article){	// TODO    $sql = "DELETE FROM articles WHERE id_article = '$id_article'";    $result=mysql_query($sql);}//// Короткое описание статьи//function articles_intro($article){}//?=substr($article['content'],0,5)?//used and no need SELECT SUBSTRING("SQL Tutorial", 5, 3) AS ExtractString;// TODO// $article - это ассоциативный массив, представляющий статью?>