<?phpinclude_once('model/startup.php');include_once('model/M_Users.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Менеджеры.$mUsers = M_Users::Instance();// Очистка старых сессий.$mUsers->ClearSessions();// Выход.$mUsers->Logout();// Обработка отправки формы.if (!empty($_POST)){	if ($mUsers->Login($_POST['login'], 	                   $_POST['password'], 					   isset($_POST['remember'])))	{		header('Location: index.php');		die();	}}// Кодировка.header('Content-type: text/html; charset=windows-1251');?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html><head>	<title>Веб-Гуру</title>	<meta content="text/html; charset=Windows-1251" http-equiv="content-type">		<link rel="stylesheet" type="text/css" media="screen" href="theme/style.css" /> </head><body>	<h1>Авторизация</h1>	<a href="index.php">Главная</a>	<form method="post">		E-mail: <input type="text" name="login" /><br/>		Пароль: <input type="password" name="password" /><br/>		<input type="checkbox" name="remember" /> Запомить меня<br/>		<input type="submit" />			</form></body></html>